import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent implements OnInit{
  title:string;
  constructor(){
    this.title='To Do'
  }

  ngOnInit(): void {

  }
}
<nav class="navbar navbar-expand-lg bg-light">
    <div class="container-fluid">
      <div class="navbar-brand d-flex" href="#">
        <div class='img_fit logo me-3 animate__animated animate__fadeInDown'>

        </div>
        <div>To Do</div>
      </div>
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"><i class="fa-solid fa-bars"></i></span>
      </button>
      <div class="collapse navbar-collapse d-lg-flex justify-content-end align-items-center" id="navbarSupportedContent">
        <ul class="navbar-nav mt-4 mt-lg-0 m-auto mb-2 mb-lg-0 w-adjust d-flex flex-column flex-lg-row justify-content-lg-end align-items-lg-center">
          <li class="nav-item d-inline-block my-1 mx-lg-4">
            <a class="nav-link active" aria-current="page" href="#">Home</a>
          </li>
          <li class="nav-item d-inline-block my-1 mx-lg-4">
            <a class="nav-link" href="#">Link</a>
          </li>
        </ul>
      </div>
    </div>
</nav>
