import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent implements OnInit{
  title:string;
  btnText:string='Sign Up';
  constructor(){
    this.title='To Do';
  }

  ngOnInit(): void {

  }
}
<nav class="navbar navbar-expand-lg bg-dark">
    <div class="container-fluid d-flex justify-content-lg-between">
      <div class="navbar-brand d-flex" href="#">
        <div class='img_fit logo me-3 animate__animated animate__fadeInDown'>

        </div>
        <div class="d-flex align-items-center text-light"><b>To Do</b></div>
      </div>
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span class='navbar-toggler-icon' style="color: white"><i class="fa-solid fa-bars"></i></span>
      </button>
      <div class="collapse navbar-collapse d-flex justify-content-lg-end align-items-center" id="navbarSupportedContent">
        <ul class="navbar-nav me-lg-3 mb-2 mb-lg-0">
          <li class="nav-item d-flex justify-content-lg-end align-items-center">
            <app-nav-button [text]="btnText"></app-nav-button>
          </li>
        </ul>
      </div>
    </div>
</nav>
